function [res,vr] = var_add_result(res, vr, newres)
% Adds new result 'newres' to results buffer 'res'. 'vr' is generated by
% calling 'var_init()'. 
%
% License:
% --------
% This is part of VAR library for automatic multidim. variation of simulation parameters.
% (c) 2018, Stanislav Maslan, s.maslan@seznam.cz
% The script is distributed under MIT license, https://opensource.org/licenses/MIT  
  
  % add result to a buffer
  vr.res_n = vr.res_n + 1;
  res{vr.res_n} = newres;
    
end